getJSON('https://8bitvape.com/api/comments/' + input)
    .then(obj => {
        if (obj) {
            if (obj.error) {
                print('not found')
                return
            }
            print(`{g}${obj.name}{/} by {b}${obj.user}{/}`)
            obj.comments.forEach(function(comment){
                print(`{p}${comment.user}:{/} {y}${comment.comment}{/} {r}${comment.rating}/5{/}`)
            })
        }
        else {
            print('not found');
        }
    })
    .catch(print.error);
