const { printTable, finishGame, getProfile, hit, showActions } = IRC.require('blackjack.game')
const { from: user } = IRC.message
const profile = getProfile( user)

if( !profile.isInGame) {
    print( `{o}${user}{/}: No active game found to {g}hit{/} on. Start a new game with .blackjack`)
} else {

    if( profile.activeHand.availableActions.hit) {
        hit( profile.game)
        printTable( profile)

        if( !profile.isInGame) {
            finishGame( profile)
        }

        profile.save()
    } else {
        print( `{o}${user}{/}: It's not possible to hit on the current hand. `)
        showActions( profile.game)
    }
}
