const { printTable, finishGame, getProfile, split, showActions } = IRC.require('blackjack.game')
const { from: user } = IRC.message
const profile = getProfile( user)

if( !profile.isInGame) {
    print( `{o}${user}{/}: No active hand found to {g}split{/}. Start a new game with .blackjack`)
} else {
    if( profile.activeHand.availableActions.split) {

        if( profile.balance < Number( profile.state.initialBet)) {
            print(`Sorry, {o}${user}{/}, you don't have enough money to double your bet.`)
            return
        }

        split( profile.game)
        profile.save()
        printTable( profile)
    } else {
        print( `{o}${user}{/}: It's not possible to split the current hand. `)
        showActions( profile.game)
    }
}
