const useGame = () => IRC.require('module.loadObject')('game', {
    captcha: false,
    scores: {},
});

input = input.trim().toLowerCase();

// leaderboard, most captchas solved

if (!module.required) {
    const [game, setGame] = useGame();
    function solved(user) {
        if (user in game.scores) {
            game.scores[user] += 1;
        } else {
            game.scores[user] = 1;
        }



        return game.scores[user];
    }
    if (!game.captcha) {
        const { q, a } = fetchSync.json('http://api.textcaptcha.com/nibblrjr.json');
        game.captcha = { q, a };
    }

    if (!input) {
        print('â˜…  ' + game.captcha.q);
    } else {
        if (game.captcha.a.includes(require('md5')(input))) {
            const { from } = IRC.message;
            print.success(`correct - ${IRC.colors.nick(from)} has solved {dc}${solved(from)} captcha`);
            delete game.captcha;
        } else {
            print.error(`incorrect - ${game.captcha.q}`)
        }
    }


    setGame(game);
}

module.exports = useGame;
