command:
const name = (IRC.command.params[0]||'').replace(/\s+/g, '');
const obj = IRC.commandFns.get(name);

if (!name || !input) {
    print.error(`usage: ${IRC.colors.cmd('command.new', null, 'name')} print(\`hello world\`)`)
}
else if (obj && obj.locked) {
    print(`{r}Error:{/} ${name} is locked`)
}
else if (obj) {
    print(`{r}Error:{/} ${name} already exists - use {p}${IRC.trigger}command.edit{/}`)
}
else {
    const status = IRC.commandFns.setSafe(name, input);
    if (status) {
        print(`{p}${IRC.trigger}${name}{/} added`)
    }
    else {
        print(`error adding {p}${IRC.trigger}${name}{/} - parent command may be locked`)
    }
}
