if (input == "UK") {
    input = "United Kingdom";
}

if (input == "Africa" ||
    input == "Asia" ||
    input == "Europe" ||
    input == "Oceania") {
    input = "_"+input;    
}

if (input == "North America" ||
    input == "South America") {
    input.replace(/\s /g, '+');
    input = "_"+input
}

getDOM(`https://www.worldcubeassociation.org/competitions?region=${encodeURIComponent(input)}`)
    .then(dom => 
        {
            const items = dom.qsa('.competition-link'); 
            var padding = "";
            for (let i = 0; i < items.length/2; i++)  {
                if (i * 2 + 1 == items.length) {
                    print(`${items[i*2].textContent.replace(/\s+/g, ' ')}`)   
                } else {
                    for (let n = 0; n < 35 - items[i * 2].textContent.replace(/\s+/g, ' ').length; n++) {
                        padding += ' ';
                    }
                    print(`${items[i*2].textContent.replace(/\s+/g,' ')}${padding}|| ${items[(i*2)+1].textContent.replace(/\s+/g, ' ')}`)
                    padding = "";
                }
            }
        }
    ).catch(print.error)
