const { window, document } = fetchSync.dom(`https://corona.help`);

const countries = [
    ...document.querySelectorAll('#countries-nav tbody tr')
].map(node => [
    node.children[0].querySelector('div').textContent,
    ...[...node.children].slice(1).map(d => d.textContent)
]);

const global = [...document.querySelectorAll('div.card-body')]
const stats = global.slice(1, 6)
    .map(d => d.textContent.replace(/\s+/g, ' ').trim())
    .map(d => ([a,...b]=d.split` `,b.join` ` + ' {o}' + a + '{/}'))

const clean = input.trim().toLowerCase();

if (input) {
    const found = countries.find(([name]) => {
        return name.toLowerCase().includes(clean)
    });
    if (found) {
        // log(found);
        const [name, infected, deaths, recovered, active] = found;
        print(`${name}  --  infected {o}${infected}{/}  deaths {r}${deaths}{/}  recovered {g}${recovered}{/}  active {o}${active}`);
    } else {
        print.error(`${input} not found`);
    }
} else {
    print(stats.join`  `)
}
