const lang = IRC.command.params[0];
const langs = ['bash', 'c', 'c++', 'c#', 'java', 'go', 'python', 'php', 'perl', 'ruby', 'rust', 'js'];
if (!lang) {
    throw new Error(`{p}${IRC.trigger}${IRC.command.path}(language){/} [code] - Supported languages: ${langs.join`, `}`);
}

if (lang == 'js') {
    throw new Error(`see {p}${IRC.trigger}help.eval{/}`)
} else if (lang == 'rust') {
    throw new Error(`see {p}${IRC.trigger}rs{/}`)
}

function getCode(input) {
    if (lang == 'bash') {
        return `#!/bin/bash\n\n${input}\n`;
    } else if (lang == 'php') {
        return `<?php ${input} ?>`;  
    } else if (lang == 'c#') {
        // Console.WriteLine("hello")
        return `using System;\n public class Hello \n{\n public static void Main() \n{\n ${input} \n} \n}`;
    } else if (lang.startsWith('c++')) {
        return `#include <iostream>\nusing namespace std;\n\n${input}`;
    } else if (lang == 'go') {
        // fmt.Println("hello")
        return `package main\n\nimport (\n    "fmt"\n)\nfunc main() { \n${input}\n }`;
    } else if (lang == 'java') {
        // System.out.println("Hello, World!");
        return `public class Main {\n\n   public static void main(String[] args) {\n\n${input}\n  }\n\n}`;
    } else {
        return input;
    }
}

getJSON('https://www.learnshell.org/', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
        code: getCode(input.replace(/  /g, '\n')),
        language: lang,
    }),
}).then((obj) => { print.raw(obj.text.trim()); }).catch((e) => {
    if (!langs.includes(lang)) {
        print(`{r}Error:{/} Unsupported language`);
    } else {
        print(`{r}Error:{/} Unknown error`);
    }
});
