// scores: [{ bytes, solution, timestamp }]

function getScores(task) {
    return load(`task-${task.toLowerCase()}`);
}

function setScores(task, obj) {
    return save(`task-${task.toLowerCase()}`, obj);
}

function getLeaderboard(name) {
    const list = Object.entries(getScores(name))
        .map(([key, value]) => ({ from: key, ...value }));
    list.sort((a, b) => a.bytes - b.bytes);
    const grouped = [/*{ bytes, scores, position, rank }*/];
    list.forEach(score => {
        const exists = grouped.find(d => d.bytes === score.bytes)
        if (exists) {
            exists.scores.push(score)
        } else {
            grouped.push({ bytes: score.bytes, scores: [score] });
        }
    });
    // arrange by timestamps
    let position = 1;
    grouped.forEach((group, i) => {
        group.scores.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp))
        group.rank = i + 1;
        group.position = position;
        position += group.scores.length;
    })
    return grouped;
}

module.exports = {
    getLeaderboard,
    getScores,
    setScores,
};
