// scores: [{ bytes, solution, timestamp }]

function loadScores(task) {
    return load(`task-${task}`);
}

function getQScore(name) {
    const list = Object.entries(loadScores(name))
        .map(([key, value]) => ({ from: key, ...value }));
    list.sort((a, b) => b.bytes - a.bytes);
    const grouped = [/*{ value, scores }*/];
    list.forEach(score => {
        const exists = groups.find(d => d.value === score.bytes)
        if (exists) {
            exists.scores.push(score)
        } else {
            grouped.push({ value: scores: [score] });
        }
    });
    return grouped;
    // arrange by timestamps
}

