});

// scores: [{ bytes, solution, timestamp }]

function loadScores(task) {
    return load(`task-${task}`);
}

function getQScore(name) {
    const list = Object.entries(loadScores(name))
        .map(([key, value]) => ({ from: key, ...value }));
    list.sort((a, b) => a.bytes - b.bytes);
    const grouped = [/*{ value, scores }*/];
    list.forEach(score => {
        const exists = grouped.find(d => d.value === score.bytes)
        if (exists) {
            exists.scores.push(score)
        } else {
            grouped.push({ value: score.bytes, scores: [score] });
        }
    });
    grouped.forEach(group => {
        group.scores.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp))
    })
    return grouped;
    // arrange by timestamps
}
