if (input == 'delete') IRC.sudo();

tasks = IRC.require('golf.tasks');
Object.keys(tasks).filter(d => d != 'wordpath').forEach(taskKey => {
    const [scores, setScores] = IRC.require('module.loadObject')(`scores-${taskKey}`);
    Object.keys(scores).forEach(key => {
        const score = scores[key];
        try {
            tasks[taskKey].test(score.solution);
        } catch (e) {
            print(`${key} has an invalid solution for ${taskKey}`);
            delete scores[key];
        }
    });
    if (input == 'delete') {
            setScores(scores);
    }
})
print('done')
