IRC.sudo();

function nuke(task, user) {
    const [scores, setScores] = IRC.require('module.loadObject')(`scores-${task}`)
    if (scores[user]) {
        print(`deleting ${task} for ${user}`)
        delete scores[user];
    }
    setScores(scores);
}

function test(shouldDelete) {
    tasks = IRC.require('golf.tasks');
    Object.keys(tasks).filter(d => d != 'wordpath').forEach(taskKey => {
        const [scores, setScores] = IRC.require('module.loadObject')(`scores-${taskKey}`);
        Object.keys(scores).forEach(key => {
            const score = scores[key];
            try {
                tasks[taskKey].test(score.solution);
            } catch (e) {
                print.error(`${key} has an invalid solution for ${taskKey}: ${e.message || e}`);
                delete scores[key];
            }
        });
        if (shouldDelete) {
            setScores(scores);
        }
    })
    print('done');
}

eval(input ? input : 'test()');
