const save = (key, data) => store.set(key, JSON.stringify(data));
const load = (key) => {
    const data = store.get(key);
    return typeof data === 'undefined' ? {} : JSON.parse(data);
};
const data = load('votes');
IRC.auth();
const tasks = Object.keys(IRC.require('golf.tasks'));
input = input.trim();
if (input === 'show') {
 	return log(data); 
}
if (!input) {
 throw ('vote for tasks to make public! you can vote for multiple tasks but once for each'); 
}
if (tasks.includes(input)) {
  if (!data[input]) {
   	data[input] = [];
  }
  if (!data[input].includes(IRC.message.from))  {
    data[input].push(IRC.message.from);
    print.success(`voted for ${IRC.colors.nick(input, false)}`);
  } else {
  	print.success('thank you for voting!');
  }
  save('votes', data);
} else {
	throw 'no such task';
}
