// ~log.regex(1) (\b(https?):\/\/[-A-Za-z0-9+&@#\/%?=~_|!:,.;]*[-A-Za-z0-9+&@#\/%=~_|])
const data = IRC.log.regex(input, IRC.command.params[0] || 3, (IRC.command.params[1] || 0) + 1);

(data.map(({time, user, command, target, message}) => (
    IRC.colors(`({dc}{u}${time}{/}) {bo}<{/}${IRC.colors.hash(user)}${user}${command=='PRIVMSG'?'':':' + command.toLowerCase()}{/}{bo}>{/}`)
    + ` ${message.replace(/\n/g, ' ')}`
)).forEach(d => print(d, { log: false })));
