const data = IRC.log.user(IRC.command.params[0] || IRC.message.from, input, IRC.command.params[1] || 3, IRC.command.params[2] || 0);

(data.map(({time, user, command, target, message}) => (
    IRC.colors(`({dc}{u}${time}{/}) {bo}<{/}${IRC.colors.hash(user)}${user}${command=='PRIVMSG'?'':':' + command.toLowerCase()}{/}{bo}>{/}`)
    + ` ${message.replace(/\n/g, ' ')}`
)).forEach(d => print.raw(d, { log: false })));
