function render({
    currency = '',
    scores = [/*{ name, score, extra }*/],
    combine = true,
    limit = 5,
    reverse = false,
    title = 'Leaderboard',
    groupPosition = false,
    fmtNum = (score) => Intl.NumberFormat().format(+score),
}) {
    const output = [];
    if (title) {
        output.push(`{u}${title}{/}`);
    }
    // assert text in extra
    scores.forEach(obj => { obj.extra = obj.extra || ''; })

    const sortScores = (combine ? combineScores(scores) : scores)
        .sort((a, b) => a.score - b.score);

    const filterScores = (!reverse ? sortScores.reverse() : sortScores)
        .slice(0, limit || undefined);

    const longestName = Math.max(15, getLongestName(filterScores));
    const longestValue = filterScores.reduce((acc, cur) => Math.max(acc, fmtNum(cur.score).length), 0) + currency.length;

    const colours = ['{y}','{gr}','{o}'];

    // name can be string / array

    let i = 0;
    filterScores.forEach(({ name, score, extra }, index) => {
        const nameRaw = Array.isArray(name) ? name.join(', ') : name;
        const fmtName = nameRaw.replace(/\w+/g, IRC.breakHighlight);
        const fmtScore = fmtNum(score);
        const number = i+1;
        const colour = colours[index] || '';
        const padding = '.'.repeat(Math.max(
            2 + longestName + longestValue - nameRaw.length - fmtScore.length - (String(number).length - 1),
            0,
        ));

        output.push(`${number}. ${colour}${fmtName}{/}{bo}${padding}{/}{y}${currency}${fmtScore}{/} ${extra}`);

        // inc
        if (!groupPosition || !Array.isArray(name)) {
            i += 1;
        } else {
            i += name.length;
        }
    });

    return output.join('\n');
}

function combineScores(scores) {
    return scores.reduce((acc, cur) => {
        const exists = acc.findIndex((d) => d.score === cur.score);
        if (exists !== -1) {
            acc[exists].name.push(cur.name);
            acc[exists].extra += ' ' + cur.extra;
        } else {
            cur.name = [cur.name];
            acc.push(cur);
        }
        return acc;
    }, []);
}

function getLongestName(scores) {
    return scores.reduce((acc, cur) => (
        Math.max(acc, Array.isArray(cur.name) ? cur.name.join(', ').length : cur.name.length)
    ), 0);
}

module.exports = render;
