command:
if (!input) {
    print(`{r}Syntax:{/} {p}${IRC.trigger}paste_source{/} name`)
}
else {
    const [command, flags] = input.split(' ');
    const source = IRC.commandFns.get(command).command;
        const data = fetchSync('https://paste.rs', {
            method: 'POST',
            body: source,
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            }
        });
        print.info(`Pasted {o}${command}{/} to ${IRC.colors.link(data.trim())}`)
}
