const items = [
    ...fetchSync
        .dom('https://peppe.rs/index.xml')
        .document.body.querySelectorAll('item'),
].map(n =>({
    title: n.querySelector('title').textContent,
    desc: _.truncate(n.querySelector('description').textContent, { length: 150 }),
}));

items.slice(0, 1)
    .forEach(({ title, desc }) => {
        print(`{g}${title}{/} ${IRC.colors.link('https://peppe.rs/posts/' + title.toLowerCase().replace(/\s/g, '_'))} ${desc}`)
    });
