function getScore(name) {
    const obj = store.get(name);
    if (typeof obj == 'undefined') {
        store.set(name, '1000');   
        return 1000;
    }
    else {
        return +obj;
    }
};
function setScore(name, amount) {
    store.set(name, String(Math.max(amount, 0)|0));
};
function addScore(name, amount) {
    setScore(name, getScore(name) + amount);
};
function subScore(name, amount) {
    setScore(name, getScore(name) - amount);
};

function addBuyin(name) {
    const obj = store.get('buyin$' + name);
    if (typeof obj == 'undefined') {
        store.set('buyin$' + name, '1');
    }
    else {
        store.set('buyin$' + name, (+obj + 1));
    }
};

function getBuyin(name) {
    const obj = store.get('buyin$' + name);
    if (typeof obj == 'undefined') {
        return 0;
    }
    else {
        return +obj;
    }
};

module.exports = {
    getScore, setScore, addScore, subScore, addBuyin, getBuyin,
};
