const [search, replace] = input.trim().split(' ');

if (!search || !replace) {
    throw 'usage: ' + IRC.colors.cmd('replace', ['text', 'replacement']);
}

const data = IRC.log.get(search, 1, 1)[0] || {};

if (data.user !== IRC.message.from) {
    print(`${IRC.message.from} thinks ${data.user} meant to say: ${data.message.replace(search, replace)}`);
} else {
    print(`${IRC.message.from} meant to say: ${data.message.replace(search, replace)}`);
}
