const matches = input.trim().match(/((?:"|').+(?:"|')|\S+)\s+((?:"|').+(?:"|')|\S+)/);

if (!matches) {
    throw 'usage: ' + IRC.colors.cmd('replace', ['text', 'replacement']);
}

const [search, replace] = matches.slice(1).map(match => match.replace(/^["'](.+)["']$/,'$1'));

const data = IRC.log.get(search, 1, 1)[0] || {};

if (data.user !== IRC.message.from) {
    print(`${IRC.breakHighlight(IRC.message.from)} thinks ${IRC.breakHighlight(data.user)} meant to say: ${data.message.replace(search, replace)}`);
} else {
    print(`${IRC.breakHighlight(IRC.message.from)} meant to say: ${data.message.replace(new RegExp(search, 'i'), replace)}`);
}
