command:
input = input.trim();
const showAll = input.includes('all');
const showStack = input.includes('stack');
const showPlays = input.includes('plays');
const [game] = IRC.require('roulette.game').useGame();

if (game.scores[input]) {
    const score = game.scores[input];
    const rebuys = game.rebuys[input] || 0;
    const plays = game.plays[input] || 1;
    const adjusted = score - (rebuys * 1000);
    print(IRC.colors.nick(input, false) + ` has {y}$${Intl.NumberFormat().format(+adjusted)}{/} ({y}$${Intl.NumberFormat().format(+score)}{/} stack with {dc}${rebuys}{/} rebuys) `)
}
else {
    const scores = Object.entries(game.scores).map(([name, score]) => {
        const rebuys = game.rebuys[name] || 0;
        const plays = game.plays[name] || 1;
        const adjusted = score - (rebuys * 1000);
        const rebuyStr = rebuys ? `({y}${rebuys}{/} rebuys - {y}$${Intl.NumberFormat().format(+adjusted)}{/})` : '({g}no{/} rebuys)';
        let extra = showStack ? rebuyStr : '';
        if (showPlays) {
            extra += ` ({dc}${plays}{/} play${plays > 1 ? 's' : ''})`;
        }
        return {
            name,
            score: showStack ? score : adjusted,
            extra,
        };
    });
    print(IRC.require('module.leaderboard')({ 
        scores,
        currency: '$',
        limit: showAll ? 0 : 5,
        reverse: input.includes('worst'),
    }));

}
