const showAll = input == 'all';
const scores = _.sortBy(store.all(), (d) => +d.value)
.reduce((acc, cur) => {
    const exists = acc.findIndex((d) => d.value === cur.value);
    if (exists !== -1) {
        acc[exists].key += `, ${IRC.breakHighlight(cur.key)}`;
    } else {
        cur.key = IRC.breakHighlight(cur.key)
        acc.push(cur);
    }
    return acc;
}, []).slice(0,Infinity).filter(d => !d.key.includes('_guesses'));

const longest = scores.reduce((acc, cur) => Math.max(acc, cur.key.length), 0) + 2;

print(`{u}Leaderboard{/}`);
const colours = ['{y}','{gr}','{o}'];
scores.reverse().forEach(({key, value}, i)=> {
    if (Number.isNaN(+value)) return;
    const pad = longest - key.length - (String(i+1).length - 1);
    print(`${i+1}. ${colours[i]||''}${key}{/}{bo}${'.'.repeat(pad>0?pad:0)}{/}{y}${+value}`)
})
print(`scrabble has been played {dc}${IRC.require('module.count').count()}{/} times`)
