function pad(num){
    if(num < 10){
        return "0" + num
    }else{
        return num
    }
}

function format_number(num){
    if(num > 5999){
        return Math.floor(num / 6000) + ":" + pad((num % 6000) / 100)
    }else{
        return +num / 100
    }
}

const obj = fetchSync(`https://www.worldcubeassociation.org/api/v0/persons/${IRC.command.params[0]}`, {type: 'json'});
const event = IRC.command.params[1]
if(obj){
    if(event in obj["personal_records"]){
        let pb = obj["personal_records"][event]
        if(event == "333fm"){
            print(`Single: ${pb["single"]["best"]}`)
            if (pb["average"]) print(`Average: ${pb["average"]["best"] / 100}`)
        }else{
            print(`Single: ${format_number(pb["single"]["best"])}`)
            if(pb["average"]){
                print(`Average: ${format_number(pb["average"]["best"])}`)
            }
        }
    }else{
        print("user hasn't done this event")
    }
}else{
    throw 'usage: ' + IRC.colors.cmd(IRC.command.path, void 0, ['wcaID', 'event']);
}
