const words = IRC.wordList.filter( x=>x.length===4)

const wordpath = input.split(/\s+/)

if( wordpath.map( w => words.includes( w)).filter( x=>x===false).length>0) {
    print( 'Some of those words are probably not real words.')
    return
}

const diff = (a, b) => {
    let count=0;
    for(let i=0; i<a.length; i++) {
        if( a[i] !== b[i]) count++
    }
    return count
}

for(let i=0; i<wordpath.length-1; i++) {
    if(diff( wordpath[i], wordpath[i+1])!==1) {
        print( wordpath[i] + ' and ' + wordpath[i+1] + ' either differ too much or are the same.')
        return
    }
}

print( "Valid wordpath!")
